import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

export interface InterestRate {
  businessDate: string;
  term: string;
  rateType: string;
  value: number;
}

@Injectable({
  providedIn: 'root'
})
export class RateService {
  // ✅ Corrected base URL
  private apiUrl = 'http://localhost:5173/api/rates';

  constructor(private http: HttpClient) {}

  addRate(rate: InterestRate): Observable<any> {
    return this.http.post(this.apiUrl, rate);
  }

  getRatesByDate(date: string): Observable<InterestRate[]> {
    return this.http.get<InterestRate[]>(`${this.apiUrl}/${date}`);
  }
}
